mapper.select:

select no, name 
  from users 
 where email=#{email } and password=#{password }


select no, name, email, gender 
  from users 
 where no=#{no }


select no, name, email, gender 
  from users 
 where email=#{email }

mapper.update:

update users 
   set name=#{name },
   

	   
    password=#{password },
    
       
        
       gender=#{gender } 
 where no = #{no }
